import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function Home() {
  const [games, setGames] = useState([]);

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/games"); // Update port if needed
        setGames(res.data);
      } catch (err) {
        console.error("Failed to fetch games:", err);
      }
    };

    fetchGames();
  }, []);

  return (
    <div className="min-h-screen bg-gray-900 text-white p-4">
      <h1 className="text-4xl font-bold text-center mb-6 text-blue-400">ðŸŽ® Ocelot Cubs</h1>

      {/* Game List */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {games.map((game) => (
          <div key={game._id} className="bg-gray-800 rounded shadow p-4 hover:scale-105 transition">
            <img
              src={game.image}
              alt={game.title}
              className="w-full h-48 object-cover rounded mb-3"
            />
            <h2 className="text-xl font-semibold mb-2">{game.title}</h2>
            <p className="text-sm text-gray-400">{game.description}</p>
          </div>
        ))}
      </div>

      {/* Optional: Login/Register */}
      <div className="flex justify-center mt-10 gap-4">
        <Link to="/login" className="px-6 py-2 bg-blue-600 hover:bg-blue-700 rounded font-bold">
          Login
        </Link>
        <Link to="/register" className="px-6 py-2 bg-green-600 hover:bg-green-700 rounded font-bold">
          Register
        </Link>
      </div>
    </div>
  );
}

export default Home;
