import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

function Home() {
  const [games, setGames] = useState([]);
  const [user, setUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch games from backend
    const fetchGames = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/games");
        setGames(res.data);
      } catch (err) {
        console.error("Failed to fetch games:", err);
      }
    };

    fetchGames();

    // Check if user is logged in
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/");
  };

  // Filter games
  const filteredGames = games.filter((game) =>
    game.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 via-gray-800 to-gray-900 text-white p-6">
      <h1 className="text-4xl font-extrabold text-center mb-8 bg-clip-text text-transparent bg-gradient-to-r from-white via-gray-200 to-white drop-shadow-lg">
        Ocelot Cubs 
      </h1>

      {/* Search Bar */}
      <div className="flex justify-center mb-8">
        <input
          type="text"
          placeholder="🔍 Search for a game..."
          className="px-5 py-3 w-full max-w-md rounded-full bg-gray-900 text-white border border-gray-500 focus:outline-none focus:border-white focus:ring-2 focus:ring-white shadow-lg placeholder-gray-400"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Game List */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {filteredGames.length > 0 ? (
          filteredGames.map((game) => (
            <div
              key={game._id}
              className="bg-gray-900 rounded-xl shadow-lg border border-gray-700 hover:shadow-white hover:border-white transition-all p-4"
            >
              <img
                src={game.image}
                alt={game.title}
                className="w-full h-48 object-cover rounded-lg mb-4 border border-gray-700"
              />
              <h2 className="text-xl font-bold text-white mb-2">
                {game.title}
              </h2>
              <p className="text-gray-300 text-sm">{game.description}</p>
            </div>
          ))
        ) : (
          <p className="text-center col-span-full text-gray-400">
            ❌ No games found
          </p>
        )}
      </div>

      {/* User Buttons */}
      <div className="flex justify-center mt-10 gap-4">
        {user ? (
          <>
            <span className="text-lg text-white">
              Hello, {user.name || "User"} 👋
            </span>
            <button
              onClick={handleLogout}
              className="px-6 py-2 bg-gradient-to-r from-red-500 to-red-700 rounded-full font-bold shadow-lg hover:shadow-red-500/50 transition-all"
            >
              Logout
            </button>
          </>
        ) : (
          <>
            <Link
              to="/login"
              className="px-6 py-2 bg-gradient-to-r from-blue-500 to-blue-700 rounded-full font-bold shadow-lg hover:shadow-blue-500/50 transition-all"
            >
              Login
            </Link>
            <Link
              to="/register"
              className="px-6 py-2 bg-gradient-to-r from-green-500 to-green-700 rounded-full font-bold shadow-lg hover:shadow-green-500/50 transition-all"
            >
              Register
            </Link>
          </>
        )}
      </div>
    </div>
  );
}

export default Home;
