import React, { useState, useEffect } from "react";

function Chat({ socket }) {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    if (!socket) return;

    socket.on("chatMessage", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => socket.off("chatMessage");
  }, [socket]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (!message.trim()) return;

    socket.emit("chatMessage", message);
    setMessage("");
  };

  return (
    <div className="min-h-screen bg-gray-900 flex flex-col items-center py-6 px-4 text-white">
      <h2 className="text-3xl font-bold text-blue-400 mb-6">
        ðŸ’¬ Chat Room
      </h2>

      {/* Chat Box */}
      <div className="w-full max-w-3xl bg-gray-800 rounded-2xl shadow-lg p-4 flex flex-col">
        <div className="flex-1 overflow-y-auto border border-gray-700 rounded-lg p-4 bg-gray-900 mb-4">
          {messages.length > 0 ? (
            messages.map((msg, index) => (
              <div
                key={index}
                className="mb-2 p-2 bg-gray-700 rounded-lg shadow-md hover:bg-gray-600 transition"
              >
                {msg}
              </div>
            ))
          ) : (
            <p className="text-gray-400 text-center">No messages yet...</p>
          )}
        </div>

        {/* Input */}
        <form onSubmit={sendMessage} className="flex gap-3">
          <input
            type="text"
            placeholder="Type a message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="flex-1 p-3 rounded-lg border border-gray-600 bg-gray-900 text-white focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
          />
          <button
            type="submit"
            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 rounded-lg font-bold shadow-lg"
          >
            Send
          </button>
        </form>
      </div>
    </div>
  );
}

export default Chat;
