import React, { useState, useEffect } from "react";

function Chat({ socket }) {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [username, setUsername] = useState("");
  const [isUsernameSet, setIsUsernameSet] = useState(false);

  useEffect(() => {
    if (!socket) return;

    socket.on("chatMessage", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => socket.off("chatMessage");
  }, [socket]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (!message.trim() || !username.trim()) return;

    const chatData = {
      user: username,
      text: message,
      time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })
    };

    socket.emit("chatMessage", chatData);
    setMessage("");
  };

  const handleSetUsername = () => {
    if (username.trim()) {
      setIsUsernameSet(true);
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center" }}>Chat Room</h2>

      {!isUsernameSet ? (
        <div style={{ marginBottom: "15px" }}>
          <input
            type="text"
            placeholder="Enter your name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ width: "100%", padding: "10px" }}
          />
          <button
            onClick={handleSetUsername}
            style={{
              marginTop: "10px",
              padding: "10px",
              width: "100%",
              backgroundColor: "#4CAF50",
              color: "white",
              border: "none",
              borderRadius: "5px"
            }}
          >
            Join Chat
          </button>
        </div>
      ) : (
        <>
          <div
            style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              height: "300px",
              overflowY: "auto",
              padding: "10px",
              background: "#f8f8f8"
            }}
          >
            {messages.map((msg, index) => (
              <div
                key={index}
                style={{
                  textAlign: msg.user === username ? "right" : "left",
                  margin: "5px 0"
                }}
              >
                <div
                  style={{
                    display: "inline-block",
                    background: msg.user === username ? "#4CAF50" : "#e0e0e0",
                    color: msg.user === username ? "white" : "black",
                    padding: "8px 12px",
                    borderRadius: "15px",
                    maxWidth: "70%"
                  }}
                >
                  <strong>{msg.user}</strong>
                  <p style={{ margin: 0 }}>{msg.text}</p>
                  <small style={{ fontSize: "10px", opacity: 0.8 }}>
                    {msg.time}
                  </small>
                </div>
              </div>
            ))}
          </div>

          <form
            onSubmit={sendMessage}
            style={{ marginTop: "10px", display: "flex", gap: "10px" }}
          >
            <input
              type="text"
              placeholder="Type a message"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              style={{ flex: 1, padding: "10px" }}
            />
            <button type="submit" style={{ padding: "10px 15px" }}>
              Send
            </button>
          </form>
        </>
      )}
    </div>
  );
}

export default Chat;
