import React, { useEffect, useState } from 'react';

function App() {
  const [games, setGames] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/games?search=${search}`);
        const data = await response.json();
        setGames(data);
      } catch (err) {
        console.error('Error fetching games:', err);
      }
    };

    fetchGames();
  }, [search]);

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>ðŸŽ® Game Library</h1>

      <input
        type="text"
        placeholder="Search for a game..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ padding: '8px', width: '300px', marginBottom: '20px' }}
      />

      {games.length === 0 ? (
        <p>No games found.</p>
      ) : (
        <div style={{ display: 'grid', gap: '20px' }}>
          {games.map((game) => (
            <div key={game._id} style={{ border: '1px solid #ccc', padding: '15px' }}>
              <h2>{game.title}</h2>
              {game.image && (
                <img src={game.image} alt={game.title} width="200" style={{ marginBottom: '10px' }} />
              )}
              <p><strong>Genre:</strong> {game.genre}</p>
              <p>{game.description}</p>
              <a href={game.downloadLink} target="_blank" rel="noreferrer">
                ðŸ”— Download
              </a>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
