import React from 'react';
import { useEffect, useState } from 'react';

function App() {
  const [msg, setMsg] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/api/message')
      .then(res => res.json())
      .then(data => {
        // ðŸ‘‡ This is how you refer to the message
        setMsg(data.message);
      });
  }, []);

  return (
    <div>
      <h1>Message from server:</h1>
      <p>{msg}</p>  {/* ðŸ‘ˆ This displays the message */}
    </div>
  );
}
