// src/pages/Home.js
import React from "react";
import { Link } from "react-router-dom";

export default function Home({ isLoggedIn }) {
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-100 to-purple-200">
      <h1 className="text-4xl font-bold text-gray-800 mb-6">Welcome to My Website</h1>
      <p className="text-lg text-gray-600 mb-8 text-center max-w-xl">
        This is the homepage. You can always access this page whether logged in or not.
      </p>
      {isLoggedIn ? (
        <Link
          to="/chat"
          className="px-6 py-3 bg-purple-500 text-white rounded-lg shadow-lg hover:bg-purple-600 transition"
        >
          Go to Chat
        </Link>
      ) : (
        <div className="space-x-4">
          <Link
            to="/login"
            className="px-6 py-3 bg-blue-500 text-white rounded-lg shadow-lg hover:bg-blue-600 transition"
          >
            Login
          </Link>
          <Link
            to="/register"
            className="px-6 py-3 bg-green-500 text-white rounded-lg shadow-lg hover:bg-green-600 transition"
          >
            Register
          </Link>
        </div>
      )}
    </div>
  );
}
