import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GameSearch = () => {
  const [query, setQuery] = useState('');
  const [games, setGames] = useState([]);

  useEffect(() => {
    const fetchGames = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/games?search=${query}`);
        setGames(res.data);
      } catch (err) {
        console.error('Error fetching games:', err);
      }
    };

    fetchGames();
  }, [query]); // re-run whenever query changes

  return (
    <div style={{ padding: '2rem' }}>
      <input
        type="text"
        placeholder="Search games..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        style={{ padding: '0.5rem', width: '300px', marginBottom: '1rem' }}
      />

      {games.length > 0 ? (
        <ul>
          {games.map((game) => (
            <li key={game._id}>
              <h3>{game.title}</h3>
              <p>{game.description}</p>
              <a href={game.downloadLink} target="_blank" rel="noreferrer">Download</a>
            </li>
          ))}
        </ul>
      ) : (
        <p>No games found.</p>
      )}
    </div>
  );
};

export default GameSearch;
