import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

function Home() {
  const [games, setGames] = useState([]);
  const [user, setUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch games from backend
    const fetchGames = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/games");
        setGames(res.data);
      } catch (err) {
        console.error("Failed to fetch games:", err);
      }
    };

    fetchGames();

    // Check if user is logged in
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/");
  };

  // Filter games based on search term
  const filteredGames = games.filter((game) =>
    game.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gray-900 text-white p-4">
      <h1 className="text-4xl font-bold text-center mb-6 text-blue-400">
        ðŸŽ® Ocelot Cubs
      </h1>

      {/* Search Bar */}
      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Search for a game..."
          className="px-4 py-2 w-full max-w-md rounded-lg bg-gray-800 text-white border border-gray-600 focus:outline-none focus:border-blue-400"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Game List */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {filteredGames.length > 0 ? (
          filteredGames.map((game) => (
            <div
              key={game._id}
              className="bg-gray-800 rounded shadow p-4 hover:scale-105 transition"
            >
              <img
                src={game.image}
                alt={game.title}
                className="w-full h-48 object-cover rounded mb-3"
              />
              <h2 className="text-xl font-semibold mb-2">{game.title}</h2>
              <p className="text-sm text-gray-400">{game.description}</p>
            </div>
          ))
        ) : (
          <p className="text-center col-span-full text-gray-400">
            No games found.
          </p>
        )}
      </div>

      {/* Buttons */}
      <div className="flex justify-center mt-10 gap-4">
        {user ? (
          <>
            <span className="text-lg">Hello, {user.name || "User"} ðŸ‘‹</span>
            <button
              onClick={handleLogout}
              className="px-6 py-2 bg-red-600 hover:bg-red-700 rounded font-bold"
            >
              Logout
            </button>
          </>
        ) : (
          <>
            <Link
              to="/login"
              className="px-6 py-2 bg-blue-600 hover:bg-blue-700 rounded font-bold"
            >
              Login
            </Link>
            <Link
              to="/register"
              className="px-6 py-2 bg-green-600 hover:bg-green-700 rounded font-bold"
            >
              Register
            </Link>
          </>
        )}
      </div>
    </div>
  );
}

export default Home;
