// frontend/src/pages/Home.js

import React from "react";
import { Link } from "react-router-dom";

function Home() {
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-4">
      <h1 className="text-4xl font-bold mb-6 text-blue-400">ðŸŽ® Ocelot Cubs</h1>

      {/* Search Bar (existing content) */}
      <input
        type="text"
        placeholder="Search for a game..."
        className="px-4 py-2 rounded mb-4 w-full max-w-md bg-gray-800 border border-gray-600 text-white"
      />

      {/* Login & Register Buttons */}
      <div className="flex gap-4 mt-6">
        <Link
          to="/login"
          className="px-6 py-2 bg-blue-600 hover:bg-blue-700 rounded font-bold"
        >
          Login
        </Link>
        <Link
          to="/register"
          className="px-6 py-2 bg-green-600 hover:bg-green-700 rounded font-bold"
        >
          Register
        </Link>
      </div>
    </div>
  );
}

export default Home;
