import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/User/login', {
        method: 'POST',
      headers: {'Content-Type': 'application/json'},
    body: JSON.stringify({ email, password }),
    });

      const data =  await res.json();
      if(res.ok) {
        localStorage.setItem('user',JSON.stringify(data));
        navigate('/');

      }else {
        alert (data.message || 'Login failed');
      }
    } catch (error) {
      alert ('Server error, please try again later');

    }
  };
    return (
      <div className = 'min-h-screen flex items-center justify-center bg-gray-900 text-white'>
        <form onSubmit = {handleLogin} className = 'bg-gray-800 p-8 rounded-shadow-mdw-full max-w-md'><h2 className = 'text-2xl font-bold mb-6 text-center'>Login</h2>
          <label className = 'block mb-2' >Email</label>
          <input
             type = 'email'
             value = {email}
             onChange = {(e) => setEmail(e.target.value)}
             className = 'w-full p-2 mb-4 rounded bg-gray-700 border border-gray-600' required/>
          
          <label className = 'block mb-2'>Password</label>
          <input
            type = 'password'
            value = {password}
            onChange = {(e) => setPassword(e.target.value)}
            className = 'w-full p-2 mb-4 rounded bg-gray-700 border border-gray-600' required/>
            
            <button
              type = 'submit'
              className = 'w-full bg-blue-600 hover:bg-blue-700 py-2 px-4 rounded font-bold'>
                Login
              </button>
            


          </form> 
      </div>
    );
  }
